var $animation_elements = $('.animation-element'),
	$window = $(window);
function check_if_in_view() {
	var i = $window.height(),
		e = $window.scrollTop(),
		n = e + i;
	$.each($animation_elements, function() {
		var i = $(this),
			t = i.outerHeight(),
			o = i.offset().top;
		o + t >= e && o <= n ? i.addClass('in-view') : i.removeClass('in-view');
	});
}
$window.on('scroll resize', check_if_in_view),
	$window.trigger('scroll'),
	$(function() {
		$('#vertical-menu .list-group-item').click(function() {
			$('#vertical-menu .list-group-item').removeClass('active'), $(this).addClass('active');
		});
	});

